(function(){'use strict';document.addEventListener('DOMContentLoaded',function(){const tooltips=document.querySelectorAll('.TT');tooltips.forEach(function(tooltip){const tooltipElement=document.createElement('div');tooltipElement.className='tooltips-wp-tip';tooltipElement.textContent=tooltip.getAttribute('data-tooltip');document.body.appendChild(tooltipElement);tooltip.addEventListener('mouseenter',showTooltip);tooltip.addEventListener('mouseleave',hideTooltip);function showTooltip(){const rect=tooltip.getBoundingClientRect();const tooltipRect=tooltipElement.getBoundingClientRect();let top,left;switch(tooltipsWpOptions.position){case 'top':top=rect.top-tooltipRect.height-10;left=rect.left+(rect.width/2)-(tooltipRect.width/2);break;case 'bottom':top=rect.bottom+10;left=rect.left+(rect.width/2)-(tooltipRect.width/2);break;case 'left':top=rect.top+(rect.height/2)-(tooltipRect.height/2);left=rect.left-tooltipRect.width-10;break;case 'right':top=rect.top+(rect.height/2)-(tooltipRect.height/2);left=rect.right+10;break}
tooltipElement.style.top=`${top + window.scrollY}px`;tooltipElement.style.left=`${left + window.scrollX}px`;tooltipElement.classList.add('active',`animation-${tooltipsWpOptions.animation}`)}
function hideTooltip(){tooltipElement.classList.remove('active')}})})})()